import { Card, CardContent, Typography, Button, Chip, Stack } from '@mui/material';
import DirectionsBusIcon from '@mui/icons-material/DirectionsBus';
import DirectionsCarIcon from '@mui/icons-material/DirectionsCar';
import DirectionsWalkIcon from '@mui/icons-material/DirectionsWalk';
import PedalBikeIcon from '@mui/icons-material/PedalBike';

export default function RouteCard({ route }) {
  const getIcon = () => {
    switch(route.type) {
      case 'bus': return <DirectionsBusIcon />;
      case 'car': 
      case 'rideshare': return <DirectionsCarIcon />;
      case 'walking': return <DirectionsWalkIcon />;
      case 'bike': return <PedalBikeIcon />;
      default: return <DirectionsBusIcon />;
    }
  };

  return (
    <Card sx={{ mb: 2 }}>
      <CardContent>
        <Stack direction="row" alignItems="center" spacing={1} sx={{ mb: 1 }}>
          {getIcon()}
          <Typography variant="h6" component="div">
            {route.name}
          </Typography>
          {route.provider && <Chip label={route.provider} size="small" />}
        </Stack>
        
        <Typography color="text.secondary" sx={{ mb: 1 }}>
          {route.time}
        </Typography>
        
        <Stack direction="row" justifyContent="space-between" alignItems="center">
          <Typography variant="h6">
            {route.price}
          </Typography>
          <Button variant="outlined" size="small">
            Add to Itinerary
          </Button>
        </Stack>
      </CardContent>
    </Card>
  );
}
