+
+  /* Transport Specific Styles */
+  .transport-type-filters {
+    display: flex;
+    gap: 8px;
+    margin-bottom: 16px;
+  }
+
+  .transport-type-btn {
+    padding: 8px 16px;
+    border-radius: 20px;
