+  const categories = [
+    { id: "all", name: "All Services" },
+    { id: "transport", name: "Transport" },
+    { id: "education", name: "Education" },
+    { id: "health", name: "Health" }
+  ];
+
+  return (
+    <div className="urban-flow-container">
+      <style>{styles}</style>
+      
+      {/* Header */}
+      <header className="header">
+        <h1 className="app-title">
