+        
+        {/* Search Bar */}
+        <div className="search-container">
+          <FiSearch className="search-icon" />
+          <input
+            type="text"
+            className="search-input"
+            placeholder="Search services..."
+            value={searchTerm}
+            onChange={(e) => setSearchTerm(e.target.value)}
+          />
+        </div>
+      </header>
+
+      {/* Category Filters */}
+      <div className="category-filters">
+        {categories.map(category => (
+          <button
+            key={category.id}
+            className={`category-btn ${activeCategory === category.id ? 'active' : 'inactive'}`}
+            onClick={() => setActiveCategory(category.id)}
+          >
+            {category.name}
+          </button>
+        ))}
+      </div>
+
+      {/* Map Toggle Button */}
+      <button
+        className="map-toggle-btn"
+        onClick={() => setShowMap(!showMap)}
+      >
+        <FiMap style={{ marginRight: '8px' }} />
+        {showMap ? "Hide Map" : "Show Map"}
+      </button>
+
+      {/* Main Content */}
+      <div>
+        {/* Services List */}
+        <div className="services-container">
+          <h2 className="services-title">
+            {activeCategory === "all" ? "All Services" : categories.find(c => c.id === activeCategory)?.name}
+          </h2>
+          <div style={{ maxHeight: '60vh', overflowY: 'auto' }}>
+            {filteredServices.length > 0 ? (
+              filteredServices.map(service => (
+                <ServiceCard
+                  key={service.id}
+                  service={service}
+                  isSelected={selectedService?.id === service.id}
+                  onClick={(service) => {
+                    setSelectedService(service);
+                    setShowMap(true);
+                  }}
+                />
+              ))
+            ) : (
+              <p className="no-services">No services found</p>
+            )}
+          </div>
+        </div>
+
+        {/* Map (Conditionally Rendered) */}
+        {showMap && (
+          <div className="map-container" style={{ marginTop: '20px' }}>
+            <MapContainer 
+              center={selectedService?.location || NAIROBI_CENTER} 
+              zoom={13} 
+              style={{ height: '100%', width: '100%' }}
+            >
+              <TileLayer
+                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
+                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
+              />
+              
+              {filteredServices.map(service => (
+                <Marker
+                  key={service.id}
+                  position={service.location}
+                  icon={createCustomIcon(
+                    service.type === 'transport' ? '#3B82F6' :
+                    service.type === 'education' ? '#F59E0B' :
+                    '#EF4444'
+                  )}
+                  eventHandlers={{
+                    click: () => setSelectedService(service)
+                  }}
+                >
+                  <Popup>
+                    <div style={{ width: '200px' }}>
+                      <h3 style={{ fontSize: '1rem', fontWeight: 600, marginBottom: '8px' }}>
+                        {service.name}
+                      </h3>
+                      <div style={{ display: 'flex', alignItems: 'center', fontSize: '0.875rem', color: '#6b7280', marginBottom: '4px' }}>
+                        <FiStar style={{ color: '#f59e0b', marginRight: '4px' }} />
+                        <span>{service.rating}</span>
+                      </div>
+                      <div style={{ fontSize: '0.875rem', marginTop: '8px' }}>
+                        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '4px' }}>
+                          <FiPhone style={{ color: '#9ca3af', marginRight: '8px' }} />
+                          <span>{service.contact}</span>
+                        </div>
+                        <div style={{ display: 'flex', alignItems: 'center' }}>
+                          <FiClock style={{ color: '#9ca3af', marginRight: '8px' }} />
+                          <span>{service.schedule}</span>
+                        </div>
+                      </div>
+                      <button style={{
+                        width: '100%',
+                        display: 'flex',
+                        alignItems: 'center',
+                        justifyContent: 'center',
+                        padding: '6px 12px',
+                        backgroundColor: '#2563eb',
+                        color: 'white',
+                        border: 'none',
+                        borderRadius: '6px',
+                        fontSize: '0.875rem',
+                        marginTop: '12px',
+                        cursor: 'pointer'
+                      }}>
+                        <FiNavigation style={{ marginRight: '8px' }} />
+                        Directions
+                      </button>
+                    </div>
+                  </Popup>
+                </Marker>
+              ))}
+            </MapContainer>
+          </div>
+        )}
+      </div>
+    </div>
+  );
+}
