+
+// Components
+const TransportTypeFilter = ({ activeType, onChange }) => {
+  const types = [
+    { id: "all", name: "All Transport", icon: <FiMap size={16} /> },
+    { id: "bus", name: "Buses", icon: <FaBus size={16} /> },
+    { id: "taxi", name: "Taxis", icon: <FaTaxi size={16} /> },
+    { id: "train", name: "Trains", icon: <FaTrain size={16} /> },
+    { id: "bike", name: "Bike Hire", icon: <FaBicycle size={16} /> },
+    { id: "boda", name: "Boda Bodas", icon: <FaMotorcycle size={16} /> }
+  ];
+
+  return (
+    <div className="transport-type-filters">
+      {types.map(type => (
+        <button
+          key={type.id}
+          className={`transport-type-btn ${activeType === type.id ? 'active' : 'inactive'}`}
+          onClick={() => onChange(type.id)}
+        >
+          <span style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
+            {type.icon}
+            {type.name}
+          </span>
+        </button>
+      ))}
+    </div>
+  );
+};
+
+const RoutePlanner = ({ onPlanRoute }) => {
+  const [start, setStart] = useState("");
+  const [end, setEnd] = useState("");
+  const [time, setTime] = useState("");
+  const [mode, setMode] = useState("transit");
+
+  const handleSubmit = () => {
+    if (!start || !end) {
+      alert("Please enter both starting point and destination");
+      return;
+    }
+    onPlanRoute({ start, end, time, mode });
+  };
+
+  return (
+    <div className="route-planner">
+      <h3><FiNavigation size={20} /> Plan Your Route</h3>
+      <div className="route-inputs">
+        <input
+          type="text"
+          placeholder="Starting point (e.g. Kencom)"
+          value={start}
+          onChange={(e) => setStart(e.target.value)}
+        />
+        <input
+          type="text"
+          placeholder="Destination (e.g. Westlands)"
+          value={end}
+          onChange={(e) => setEnd(e.target.value)}
+        />
+        <input
+          type="time"
+          value={time}
+          onChange={(e) => setTime(e.target.value)}
+          placeholder="Departure time"
+        />
+        <select
+          value={mode}
+          onChange={(e) => setMode(e.target.value)}
+        >
+          <option value="transit">Public Transit</option>
+          <option value="walking">Walking</option>
+          <option value="driving">Driving</option>
+        </select>
+      </div>
+      <button 
+        className="plan-route-btn"
+        onClick={handleSubmit}
+      >
+        <FiNavigation size={16} /> Find Best Route
+      </button>
+    </div>
+  );
+};
+
+const BikeHireCard = ({ station, onHire }) => {
+  return (
+    <div className="bike-hire-card">
+      <div className="bike-station-info">
+        <h4>{station.name}</h4>
+        <div className="bike-availability">
+          <div className="availability-item">
+            <FaBicycle size={14} />
+            <span>{station.bikesAvailable} bikes</span>
+          </div>
+          <div className="availability-item">
+            <BsPinMapFill size={14} />
+            <span>{station.slotsAvailable} slots</span>
+          </div>
+        </div>
+        <div className="bike-pricing">{station.price}</div>
+      </div>
+      <button 
+        className="hire-btn"
+        onClick={() => onHire(station)}
+      >
+        <FiCreditCard size={14} /> Hire
+      </button>
+    </div>
+  );
+};
+
+const ItineraryPanel = ({ items, onRemove }) => {
+  const [expanded, setExpanded] = useState(true);
+
+  return (
+    <div className="itinerary-panel">
+      <h3 onClick={() => setExpanded(!expanded)} style={{ cursor: 'pointer' }}>
+        <IoMdTime size={20} /> Your Itinerary
+        {expanded ? <FiChevronUp size={20} /> : <FiChevronDown size={20} />}
+      </h3>
+      
+      {expanded && (
+        <>
+          {items.length === 0 ? (
+            <p className="empty-itinerary">No items in your itinerary yet. Add services to plan your day!</p>
+          ) : (
+            <ul className="itinerary-list">
+              {items.map((item, index) => (
+                <li key={index} className="itinerary-item">
+                  <div className="item-details">
+                    <span className="item-name">{item.name}</span>
+                    <span className="item-time">
+                      <FiClock size={12} /> {item.time}
+                      {item.price && <span> ‚Ä¢ {item.price}</span>}
+                    </span>
+                  </div>
+                  <button 
+                    className="remove-btn"
+                    onClick={() => onRemove(index)}
+                  >
+                    <FiX size={12} /> Remove
+                  </button>
+                </li>
+              ))}
+            </ul>
+          )}
+        </>
+      )}
+    </div>
+  );
+};
+
+const ServiceDetails = ({ service, onAddToItinerary }) => {
+  if (!service) return null;
+
+  const renderTransportDetails = () => {
+    if (service.type === "bus" || service.type === "train") {
+      return (
+        <div className="detail-section">
+          <h4><FaBus size={18} /> Routes</h4>
+          {service.routes.map((route, index) => (
+            <div key={index} className="route-details">
+              <div className="route-header">
+                <span className="route-name">{route.name}</span>
+                <span>{route.price}</span>
+              </div>
+              <div className="route-body">
+                <div className="route-stats">
+                  <div className="stat-item">
+                    <div className="stat-value">{route.duration}</div>
+                    <div className="stat-label">Duration</div>
+                  </div>
+                  <div className="stat-item">
+                    <div className="stat-value">{route.stops}</div>
+                    <div className="stat-label">Stops</div>
+                  </div>
+                  <div className="stat-item">
+                    <div className="stat-value">{route.frequency}</div>
+                    <div className="stat-label">Frequency</div>
+                  </div>
+                </div>
+                {service.type === "bus" && service.popularStations && (
+                  <div style={{ marginBottom: '16px' }}>
+                    <div className="detail-label">Popular Stations:</div>
+                    <div className="detail-value">
+                      {service.popularStations.join(", ")}
+                    </div>
+                  </div>
+                )}
+                {service.type === "train" && service.stations && (
+                  <div style={{ marginBottom: '16px' }}>
+                    <div className="detail-label">Stations:</div>
+                    <div className="detail-value">
+                      {service.stations.join(" ‚Üí ")}
+                    </div>
+                  </div>
+                )}
+              </div>
+            </div>
+          ))}
+        </div>
+      );
+    } else if (service.type === "taxi") {
+      return (
+        <div className="detail-section">
+          <h4><FaTaxi size={18} /> Vehicle Options</h4>
+          {service.vehicleTypes.map((vehicle, index) => (
+            <div key={index} style={{ 
+              background: '#f8fafc', 
+              padding: '12px', 
+              borderRadius: '8px', 
+              marginBottom: '12px' 
+            }}>
+              <div style={{ 
+                display: 'flex', 
+                justifyContent: 'space-between',
+                marginBottom: '8px',
+                fontWeight: '600'
+              }}>
+                <span>{vehicle.name}</span>
+                <span>{vehicle.price}</span>
+              </div>
+              <div style={{ fontSize: '14px', color: '#64748b' }}>
+                Capacity: {vehicle.capacity} passengers
