+          {service.estimatedWait && (
+            <div className="detail-row">
+              <span className="detail-label">Estimated Wait:</span>
+              <span className="detail-value">{service.estimatedWait}</span>
+            </div>
+          )}
+        </div>
+      );
+    } else if (service.type === "boda") {
+      return (
+        <div className="detail-section">
+          <h4><FaMotorcycle size={18} /> Safety Tips</h4>
+          <ul style={{ paddingLeft: '20px', marginTop: '8px' }}>
+            {service.safetyTips.map((tip, index) => (
+              <li key={index} style={{ marginBottom: '8px' }}>{tip}</li>
+            ))}
+          </ul>
+        </div>
+      );
+    }
+  };
+
+  const renderEducationDetails = () => {
+    if (service.type === "school" || service.type === "university") {
+      return (
+        <>
+          <div className="detail-section">
+            <h4><FaSchool size={18} /> Programs</h4>
+            <div className="features-list">
+              {service.programs.map((program, index) => (
+                <span key={index} className="feature-tag">{program}</span>
+              ))}
+            </div>
+          </div>
+          
+          {service.facilities && (
+            <div className="detail-section">
+              <h4><FiInfo size={18} /> Facilities</h4>
+              <div className="features-list">
+                {service.facilities.map((facility, index) => (
+                  <span key={index} className="feature-tag">{facility}</span>
+                ))}
+              </div>
+            </div>
+          )}
+          
+          {service.transportOptions && (
+            <div className="detail-section">
+              <h4><FaBus size={18} /> Transport Options</h4>
+              <div className="detail-value">
+                {Array.isArray(service.transportOptions) 
+                  ? service.transportOptions.join(", ")
+                  : service.transportOptions}
+              </div>
+            </div>
+          )}
+        </>
+      );
+    }
+  };
+
+  const renderHealthDetails = () => {
+    if (service.type === "hospital" || service.type === "clinic") {
+      return (
+        <>
+          {service.emergency && (
+            <div className="detail-row">
+              <span className="detail-label">Emergency:</span>
+              <span className="detail-value" style={{ color: '#ef4444', fontWeight: '600' }}>
+                {service.emergency}
+              </span>
+            </div>
+          )}
+          
+          {service.visitingHours && (
+            <div className="detail-row">
+              <span className="detail-label">Visiting Hours:</span>
+              <span className="detail-value">{service.visitingHours}</span>
+            </div>
+          )}
+          
+          {service.averageWaitTime && (
+            <div className="detail-row">
+              <span className="detail-label">Average Wait:</span>
+              <span className="detail-value">{service.averageWaitTime}</span>
+            </div>
+          )}
+          
+          {service.specialists && (
+            <div className="detail-section">
+              <h4><FiUser size={18} /> Specialists</h4>
+              <div className="features-list">
+                {service.specialists.map((specialist, index) => (
+                  <span key={index} className="feature-tag">{specialist}</span>
+                ))}
+              </div>
+            </div>
+          )}
+          
+          {service.insurance && (
+            <div className="detail-section">
+              <h4><FiCreditCard size={18} /> Accepted Insurance</h4>
+              <div className="features-list">
+                {service.insurance.map((insurer, index) => (
+                  <span key={index} className="feature-tag">{insurer}</span>
+                ))}
+              </div>
+            </div>
+          )}
+        </>
+      );
+    } else if (service.type === "pharmacy") {
+      return (
+        <>
+          {service.delivery && (
+            <div className="detail-row">
+              <span className="detail-label">Delivery:</span>
+              <span className="detail-value">{service.delivery}</span>
+            </div>
+          )}
+          
+          {service.services && (
+            <div className="detail-section">
+              <h4><FaClinicMedical size={18} /> Services</h4>
+              <div className="features-list">
+                {service.services.map((serviceItem, index) => (
+                  <span key={index} className="feature-tag">{serviceItem}</span>
+                ))}
+              </div>
+            </div>
+          )}
+        </>
+      );
+    }
+  };
+
+  const renderEntertainmentDetails = () => {
+    if (service.type === "mall") {
+      return (
+        <>
+          {service.popularStores && (
+            <div className="detail-section">
+              <h4><MdShoppingCart size={18} /> Popular Stores</h4>
+              <div className="features-list">
+                {service.popularStores.map((store, index) => (
+                  <span key={index} className="feature-tag">{store}</span>
+                ))}
+              </div>
+            </div>
+          )}
+          
+          {service.parking && (
+            <div className="detail-row">
+              <span className="detail-label">Parking:</span>
+              <span className="detail-value">{service.parking}</span>
+            </div>
+          )}
+        </>
+      );
+    } else if (service.type === "restaurant") {
+      return (
+        <>
+          <div className="detail-section">
+            <h4><MdRestaurant size={18} /> Cuisine</h4>
+            <div className="features-list">
+              {service.cuisine.map((type, index) => (
+                <span key={index} className="feature-tag">{type}</span>
+              ))}
+            </div>
+          </div>
+          
+          {service.signatureDish && (
+            <div className="detail-row">
+              <span className="detail-label">Signature Dish:</span>
+              <span className="detail-value">{service.signatureDish}</span>
+            </div>
+          )}
+          
+          {service.entertainment && (
+            <div className="detail-row">
+              <span className="detail-label">Entertainment:</span>
+              <span className="detail-value">{service.entertainment}</span>
+            </div>
+          )}
+        </>
+      );
+    } else if (service.type === "museum") {
+      return (
+        <>
+          {service.exhibits && (
+            <div className="detail-section">
+              <h4><BsPinMapFill size={18} /> Exhibits</h4>
+              <div className="features-list">
+                {service.exhibits.map((exhibit, index) => (
+                  <span key={index} className="feature-tag">{exhibit}</span>
+                ))}
+              </div>
+            </div>
+          )}
+          
+          {service.entranceFee && (
+            <div className="detail-row">
+              <span className="detail-label">Entrance Fee:</span>
+              <span className="detail-value">{service.entranceFee}</span>
+            </div>
+          )}
+        </>
+      );
+    }
+  };
+
+  const getServiceTypeDetails = () => {
+    switch (service.type) {
+      case "bus":
+      case "taxi":
+      case "train":
+      case "boda":
+        return renderTransportDetails();
+      case "school":
+      case "university":
+        return renderEducationDetails();
+      case "hospital":
+      case "clinic":
+      case "pharmacy":
+        return renderHealthDetails();
+      case "mall":
+      case "restaurant":
+      case "museum":
+        return renderEntertainmentDetails();
+      default:
+        return null;
+    }
+  };
+
+  const handleAddToItinerary = () => {
+    const itineraryItem = {
+      id: Date.now(),
+      name: service.name,
+      time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
+      price: service.price || service.fees,
+      type: service.type
+    };
+    onAddToItinerary(itineraryItem);
+  };
+
+  return (
+    <div className="service-details">
+      <div className="service-details-header">
+        <h3>{service.name}</h3>
+        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
+          <FiStar className="star-icon" />
+          <span>{service.rating}</span>
+        </div>
+      </div>
+      
+      <div className="detail-section">
+        <h4><FiInfo size={18} /> Basic Information</h4>
+        <div className="detail-row">
+          <span className="detail-label">Contact:</span>
+          <span className="detail-value">
+            <a href={`tel:${service.contact}`} style={{ color: '#2563eb', textDecoration: 'none' }}>
+              {service.contact}
+            </a>
+            {service.emergency && (
+              <span style={{ marginLeft: '12px' }}>
+                <span className="detail-label">Emergency:</span>
+                <span className="detail-value" style={{ color: '#ef4444', fontWeight: '600' }}>
+                  <a href={`tel:${service.emergency}`} style={{ color: '#ef4444', textDecoration: 'none' }}>
+                    {service.emergency}
+                  </a>
+                </span>
+              </span>
+            )}
+          </span>
+        </div>
+        
+        {service.location && (
+          <div className="detail-row">
+            <span className="detail-label">Location:</span>
+            <span className="detail-value">
+              {service.location[0].toFixed(4)}, {service.location[1].toFixed(4)}
+            </span>
+          </div>
+        )}
+        
+        {service.schedule && (
+          <div className="detail-row">
+            <span className="detail-label">Hours:</span>
+            <span className="detail-value">{service.schedule}</span>
+          </div>
+        )}
+        
+        {service.price && (
+          <div className="detail-row">
+            <span className="detail-label">Price:</span>
+            <span className="detail-value">{service.price}</span>
+          </div>
+        )}
+        
+        {service.fees && (
+          <div className="detail-row">
+            <span className="detail-label">Fees:</span>
+            <span className="detail-value">{service.fees}</span>
+          </div>
+        )}
+        
+        {service.operator && (
+          <div className="detail-row">
+            <span className="detail-label">Operator:</span>
+            <span className="detail-value">{service.operator}</span>
+          </div>
+        )}
+      </div>
+      
+      {service.features && (
+        <div className="detail-section">
+          <h4><FiStar size={18} /> Features</h4>
+          <div className="features-list">
+            {service.features.map((feature, index) => (
+              <span key={index} className="feature-tag">{feature}</span>
+            ))}
+          </div>
+        </div>
+      )}
+      
+      {getServiceTypeDetails()}
+      
+      <div className="action-btns">
+        <button className="action-btn" onClick={handleAddToItinerary}>
+          <FiPlus size={16} /> Add to Itinerary
+        </button>
+        <button className="action-btn secondary">
+          <MdDirections size={16} /> Directions
+        </button>
+        {service.contact && (
+          <a href={`tel:${service.contact}`} style={{ textDecoration: 'none', flex: 1 }}>
+            <button className="action-btn">
+              <FiPhoneCall size={16} /> Call Now
+            </button>
+          </a>
+        )}
+      </div>
+    </div>
+  );
+};
+
+const ServiceCard = ({ service, isSelected, onClick }) => {
+  const iconColors = {
+    transport: '#3B82F6',
+    bus: '#3B82F6',
+    taxi: '#10B981',
+    train: '#8B5CF6',
+    bike: '#10B981',
+    boda: '#F59E0B',
+    education: '#F59E0B',
+    school: '#F59E0B',
+    university: '#F59E0B',
+    health: '#EF4444',
+    hospital: '#EF4444',
+    clinic: '#EF4444',
+    pharmacy: '#EF4444',
+    entertainment: '#8B5CF6',
+    mall: '#8B5CF6',
+    restaurant: '#8B5CF6',
+    museum: '#8B5CF6'
+  };
+
+  const getServiceDescription = () => {
+    if (service.type === "bus" || service.type === "train") {
+      return service.routes?.[0]?.name || "Multiple routes available";
+    } else if (service.type === "taxi") {
+      return service.vehicleTypes?.[0]?.name || "Various vehicle options";
+    } else if (service.type === "bike") {
+      return `${service.bikesAvailable} bikes available`;
+    } else if (service.type === "school" || service.type === "university") {
+      return service.programs?.join(", ") || "Educational institution";
+    } else if (service.type === "hospital" || service.type === "clinic") {
+      return service.services?.join(", ") || "Medical services";
+    } else if (service.type === "mall") {
+      return "Shopping center";
+    } else if (service.type === "restaurant") {
+      return service.cuisine?.join(", ") || "Dining establishment";
+    }
+    return "";
+  };
+
+  return (
+    <div 
+      className={`service-card ${isSelected ? 'selected' : 'unselected'}`}
+      onClick={() => onClick(service)}
+    >
+      <div className="service-card-content">
+        <div 
+          className="service-icon" 
+          style={{ 
+            backgroundColor: `${iconColors[service.type]}20`,
+            color: iconColors[service.type]
+          }}
+        >
+          {service.icon}
+        </div>
+        <div style={{ flex: 1 }}>
+          <h3 className="service-name">{service.name}</h3>
+          <p className="service-description">{getServiceDescription()}</p>
+          <div className="service-meta">
+            <span style={{ display: 'flex', alignItems: 'center' }}>
+              <FiStar className="star-icon" />
+              <span>{service.rating}</span>
+            </span>
+            <span className="divider">‚Ä¢</span>
+            <span>{service.price || service.fees || "See pricing"}</span>
+            <span className="divider">‚Ä¢</span>
+            <span className="service-tag">{service.type}</span>
+          </div>
+          <div className="service-meta">
+            <FiClock size={14} />
+            <span>{service.schedule || service.operatingHours || "Check schedule"}</span>
+          </div>
+        </div>
+      </div>
+    </div>
+  );
+};
+
+// Main App Component
+export default function UrbanFlow() {
+  const [selectedService, setSelectedService] = useState(null);
+  const [searchTerm, setSearchTerm] = useState("");
+  const [activeCategory, setActiveCategory] = useState("transport");
+  const [transportType, setTransportType] = useState("all");
+  const [showMap, setShowMap] = useState(false);
+  const [itinerary, setItinerary] = useState([]);
+  const [mapZoom, setMapZoom] = useState(13);
+
+  const handlePlanRoute = (routeDetails) => {
+    const suggestedRoutes = [
+      {
+        id: Date.now(),
+        name: `${routeDetails.start} to ${routeDetails.end}`,
+        transportType: routeDetails.mode === "walking" ? "walking" : 
+                     routeDetails.mode === "driving" ? "driving" : "bus",
+        duration: routeDetails.mode === "walking" ? "45 min" : 
+                 routeDetails.mode === "driving" ? "20 min" : "35 min",
+        price: routeDetails.mode === "walking" ? "Free" : 
+              routeDetails.mode === "driving" ? "Ksh 300-500 (fuel)" : "Ksh 120",
+        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
+        steps: routeDetails.mode === "walking" ? [
+          "Start at " + routeDetails.start,
+          "Walk along Kenyatta Avenue (15 min)",
+          "Turn right onto Waiyaki Way (20 min)",
+          "Arrive at " + routeDetails.end
+        ] : routeDetails.mode === "driving" ? [
+          "Start at " + routeDetails.start,
+          "Take Uhuru Highway (10 min)",
+          "Exit at Westlands roundabout (5 min)",
+          "Arrive at " + routeDetails.end
+        ] : [
+          "Walk to bus stop (5 min)",
+          "Take Route 23 to Downtown (15 min)",
+          "Transfer to Route 42 (10 min)",
+          "Walk to destination (5 min)"
+        ]
+      }
+    ];
+    
+    setItinerary(prev => [...prev, ...suggestedRoutes]);
+  };
+
+  const handleHireBike = (station) => {
+    const hireDetails = {
+      id: Date.now(),
+      name: `Bike hire at ${station.name}`,
+      time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
+      price: station.price,
+      station: station.name,
+      type: "bike"
+    };
+    
+    setItinerary(prev => [...prev, hireDetails]);
+  };
+
+  const handleAddToItinerary = (item) => {
+    setItinerary(prev => [...prev, item]);
+  };
+
+  const removeFromItinerary = (index) => {
+    setItinerary(prev => prev.filter((_, i) => i !== index));
+  };
+
+  const clearSearch = () => {
+    setSearchTerm("");
+  };
+
+  const filteredServices = services[activeCategory]?.filter(service =>
+    service.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
+    (service.type && service.type.toLowerCase().includes(searchTerm.toLowerCase())) ||
+    (service.operator && service.operator.toLowerCase().includes(searchTerm.toLowerCase()))
+  ) || [];
+
+  const categories = [
+    { id: "transport", name: "Transport", icon: <FaBus size={16} /> },
+    { id: "education", name: "Education", icon: <FaSchool size={16} /> },
+    { id: "health", name: "Health", icon: <FaHospital size={16} /> },
+    { id: "entertainment", name: "Entertainment", icon: <MdShoppingCart size={16} /> }
+  ];
+
+  const handleMapZoomIn = () => {
+    setMapZoom(prev => Math.min(prev + 1, 18));
+  };
+
+  const handleMapZoomOut = () => {
+    setMapZoom(prev => Math.max(prev - 1, 10));
+  };
+
+  const handleMapCenter = () => {
+    setSelectedService(null);
+  };
+
+  return (
+    <div className="urban-flow-container">
+      <style>{styles}</style>
+      
+      {/* Header */}
+      <header className="header">
+        <h1 className="app-title">
+          <span>Urban</span> Flow
+        </h1>
+        <p className="app-subtitle">Navigate Nairobi's transport network and essential services with ease</p>
+        
+        {/* Search Bar */}
+        <div className="search-container">
+          <FiSearch className="search-icon" />
+          <input
+            type="text"
+            className="search-input"
+            placeholder="Search services, routes, or locations..."
+            value={searchTerm}
+            onChange={(e) => setSearchTerm(e.target.value)}
+          />
+          {searchTerm && (
+            <button className="clear-search" onClick={clearSearch}>
+              <FiX />
+            </button>
+          )}
+        </div>
+      </header>
+
+      {/* Category Filters */}
+      <div className="category-filters">
+        {categories.map(category => (
+          <button
+            key={category.id}
+            className={`category-btn ${activeCategory === category.id ? 'active' : 'inactive'}`}
+            onClick={() => {
+              setActiveCategory(category.id);
+              setTransportType("all");
+              setSelectedService(null);
+            }}
+          >
+            <span style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
+              {category.icon}
+              {category.name}
+            </span>
+          </button>
+        ))}
+      </div>
+
+      {/* Map Toggle Button */}
+      <button
