-                {filteredServices
-                  .filter(service => transportType === "all" || service.type === transportType)
-                  .map(service => (
-                    <ServiceCard
-                      key={service.id}
-                      service={service}
-                      isSelected={selectedService?.id === service.id}
-                      onClick={(service) => {
-                        setSelectedService(service);
-                        setShowMap(true);
-                      }}
-                    />
-                  ))
-                }
