import { useState } from 'react';
import TransportPlanner from '../components/TransportPlanner';
import TransportOptions from '../components/TransportOptions';

export default function Transport() {
  const [origin, setOrigin] = useState('JFK');
  const [destination, setDestination] = useState('LXX');
  const [routes, setRoutes] = useState([
    {
      id: 1,
      name: 'Express Bus 42',
      time: '10:15 AM - 10:40 AM',
      price: '$2.50',
      type: 'bus'
    },
    {
      id: 2,
      name: 'City Bus 15',
      time: '10:10 AM - 10:45 AM',
      price: '$2.00',
      type: 'bus'
    },
    {
      id: 3,
      name: 'Rideshare',
      time: '10:12 AM - 10:25 AM',
      price: '$12.75',
      type: 'rideshare',
      provider: 'Uber'
    }
  ]);

  return (
    <div className="transport-page">
      <h1>UrbanFlow</h1>
      <h2>Transport Planner</h2>
      <p>Plan your journey across the city using multiple transport options</p>
      
      <TransportPlanner 
        origin={origin}
        destination={destination}
        setOrigin={setOrigin}
        setDestination={setDestination}
      />
      
      <TransportOptions 
        routes={routes}
        origin={origin}
        destination={destination}
      />
    </div>
  );
}
